templates.template:

        /* Enter a unique ExecutionPlan */
        @Plan:name('SensorAnalyticsPlan')

        /* Enter a unique description for ExecutionPlan */
        -- @Plan:description('ExecutionPlan')

        /* define streams/tables and write queries here ... */

        @Import('org.wso2.event.$sensorType.stream:1.0.0')
        define stream InputStream (sensor_id string, sensor_value double);

@Export('org.wso2.event.$sensorType.statistics.stream:1.0.0')
define stream OutputStream (sensor_type string, sensor_id_distinct_count long, count long, sum double, average double, max double, min double);

from InputStream#window.time($timeInMins min)
select '$sensorType' as sensor_type, distinctcount(sensor_id) as sensor_id_distinct_count, count() as count, math:round(sum(sensor_value)*100.0)/100.0 as sum, math:round(avg(sensor_value)*100.0)/100.0 as average, max(sensor_value) as  max, min(sensor_value) as min
insert into OutputStream;

artifacts.artifact:

    var getGadgetLocation = function (callback) {
    var gadgetLocation = "/portal/store/carbon.super/fs/gadget/$sensorType-count-chart";
    var PATH_SEPERATOR = "/";
    if (gadgetLocation.search("store") != -1) {
        wso2.gadgets.identity.getTenantDomain(function (tenantDomain) {
            var gadgetPath = gadgetLocation.split(PATH_SEPERATOR);
            var modifiedPath = '';
            for (var i = 1; i < gadgetPath.length; i++) {
                if (i === 3) {
                    modifiedPath = modifiedPath.concat(PATH_SEPERATOR, tenantDomain);
                } else {
                    modifiedPath = modifiedPath.concat(PATH_SEPERATOR, gadgetPath[i])
                }
            }
            callback(modifiedPath);
        });
    } else {
        callback(gadgetLocation);
    }
}


